css:
#clockdiv {
    font-family: sans-serif;
    color: #333;
    display: inline-block;
    text-align: center;
    font-size: 0;
}
#clockdiv > div {
    padding: 12px 8px;
    display: inline-block;
    width: 150px;
    font-size: 70px;
}
#clockdiv div > span {
    text-align: center;
    padding: 10px 0px;
    width: 100%;
    border-radius: 20px;
    font-weight: bold;
    display: inline-block;
    color: #1b1e56;
    border: 2px solid #1b1e56;
}
.smalltext {
    padding-top: 10px;
    font-size: 30px;
}
.warning > #clockdiv > div > span {
    border-color: darkgoldenrod;
    color: darkgoldenrod;
}
.finished > #clockdiv > div > span {
    border-color: darkred;
    color: darkred;
}
