css:
body {
    font-family: "Helvetica", "Arial", sans-serif;
    line-height: 1;
}

.img-circle {
    width: 52px;
}

.container-main {
    height: 420px !important;
    width: 820px !important;
    float: left;
    margin-top: 25px;
}
.row-finance {
    height: 60px;
    background: #e0e0e0;
    margin-bottom: 17px;
    border-left: #264a88 10px solid;
}

.row-finance > div {
    float: left;
}

.value {
font-size: 20px;
    padding-top: 20px;;
    color: #000;
    white-space: nowrap;
}
.down-arrow {
    margin: 25px 0px 0px 20px;
    width: 0;
    height: 0;
    border-left: 10px solid transparent;
    border-right: 10px solid transparent;
    border-top: 10px solid red;
}
.up-arrow {
    margin: 25px 0px 0px 20px;
    width: 0;
    height: 0;
    border-left: 10px solid transparent;
    border-right: 10px solid transparent;
    border-bottom: 10px solid green;
}
.right-arrow {
    margin: 20px 0px 0px 25px;
    width: 0;
    height: 0;
    border-left: 10px solid gray;
    border-top: 10px solid transparent;
    border-bottom: 10px solid transparent;
}
.variant {
    font-size: 20px;
    padding-top: 17px;
}
.flags {
    padding-top: 4px;
    width: 15%;
}
.name {
    width: 20%;
}
.price {
    width: 20%;
    margin-left: 15%;
    text-align: right;
}
.perc {
    width: 20%;
    text-align: right;
}
.arrow {
    width: 8%;
    margin-left: 2%;
}
.value-up {
    color: green;
    font-weight: bold
}
.value-down {
    color: red;
    font-weight: bold
}
.value-equal {
    color: gray;
    font-weight: bold
}
.name-stock {
    color: gray;
}
