module.onRender:

var item = items[0];
var interval;

$(function() {
    loadImage();
});

function loadImage() {
    $("#dashboard").html("");
    var image = new Image();
    image.onload = function() {
        $("#dashboard").append($("<img src=\"" + this.src + "\" >"));
        $("#loader").hide();

        if (interval) {
            clearInterval(interval)
        }
        interval = setInterval(loadImage, properties.updateInterval * 60 * 1000);
    }

    image.onerror = function() {
        console.log('error')
        $("#loader").show();
        if (interval) {
            clearInterval(interval)
        }
        interval = setInterval(loadImage, 60 * 1000);
    }

    if (item.url) {
        if (item.isPreview) {
            item.url = item.url.concat("&isDebug=1")
        }

        image.src = item.url;
    }
}
