css:
body {
    font-family:Arial;
    margin:0;
    padding-top: 5px;
}

.icon {
    float: left;
    background: url([[ICONS]]) no-repeat top left;
}

.temperature {
    font-size: 46px;
}

.desc {
    padding-top: 20px;
    font-size: 34px;
}

.powered-by {
    font-size: 12px;
    clear: both;
    text-align:center;
}
