css:
* {
  -webkit-box-sizing: border-box;
  -moz-box-sizing: border-box;
  box-sizing: border-box;
}

:after, :before {
  -webkit-box-sizing: border-box;
  -moz-box-sizing: border-box;
  box-sizing: border-box;
}

.container {
  width: 960px !important;
  height: 340px !important;
  margin-right: auto;
  margin-left: auto;
}

.container-row {
  padding: 30px;
  padding-top: 50px;
  display: table;
  width: 100%;
}

.text-center {
  text-align: center;
}

body {
  width: 960px !important;
  height: 340px !important;
  font-family: "Helvetica", "Arial", sans-serif;
  line-height: 1;
}

.shadowed {
  text-shadow: 0px 0px 2px rgba(255, 255, 255, 0.5);
  filter: dropshadow(color=rgba(255, 255, 255, 0.5), offx=2, offy=2);
}

.current-temp {
  font-family: "Helvetica", sans-serif;
  font-weight: bold;
  font-size: 100px;
}

.big-icon {
  font-size: 90px;
  padding-bottom: 30px;

}

.big-temp {
  padding: 10px 0px;
  font-size: 45px;

}

.small-day {
  font-size: 20px;
  font-style: italic;
  font-weight: lighter;

}

.small-icon {
  font-size: 30px;
  padding: 50px 0px 10px 0px;

}

.small-temp {
  padding: 10px 0px;
  font-size: 16px;

}

.container-footer {
 float: left;
 width: 100%;
 }

.powered {
  font-size: 14px;
 text-align: center;

}

.details-box {
  font-size: 26px;
 height: 220px;
  padding-left: 30px;
  width: 20%;
  float: left;
}

.upcoming-days {
  height: 195px;
  margin-top: 20px !important;
  text-align: center;
  width: 20%;
  float: left;
}

.selector-day {
  font-size: 15px;
  font-weight: light;
  margin-top: 25px;
}

.selector-temp {
  font-size: 25px;
  font-weight: bold;
  font-style: italic;
}
