css:
* {
  -webkit-box-sizing: border-box;
  -moz-box-sizing: border-box;
  box-sizing: border-box;
}

:after, :before {
  -webkit-box-sizing: border-box;
  -moz-box-sizing: border-box;
  box-sizing: border-box;
}

.text-center {
  text-align: center;
}

body {
  width: 340px !important;
  height: 200px !important;
  font-family: "Helvetica", "Arial", sans-serif;
  line-height: 1;
}

.description-container {
 width: 100%;
 float: left;
 }

.shadowed {
  text-shadow: 0px 0px 2px rgba(255, 255, 255, 0.5);
  filter: dropshadow(color=rgba(255, 255, 255, 0.5), offx=2, offy=2);
}

.container {
  padding-right: 15px;
  padding-left: 15px;
  margin-right: auto;
  margin-left: auto;
  background-position: center;
  width: 340px !important;
  height: 200px !important;
}

.main-panel {
  height: 137px;
}

.container-row {
  height: 200px;
  position: relative;

}

.big-container {
  float: left;
  width: 50%;
}

.big-day {
  font-size: 30px;
  font-style: italic;
  font-weight: lighter;
}

.big-icon {
  font-size: 90px;
}

.big-temp {
 font-size: 55px;
 margin-top: 20px;
 }

.big-temp sup {
 font-size: 25px;
 }

.description {
  font-size: 45px;
}

.details-box {
   display: table;
 padding-top: 40px;
  width: 100%;
}

.powered {
 position: absolute;
 left: 0;
 bottom: 8px;
 text-align: center;
 width: 100%;
 }
