css:
* {
  -webkit-box-sizing: border-box;
  -moz-box-sizing: border-box;
  box-sizing: border-box;
}

:after, :before {
  -webkit-box-sizing: border-box;
  -moz-box-sizing: border-box;
  box-sizing: border-box;
}


#content {
  color: #272625;
}

.container {
  width: 340px !important;
  height: 200px !important;
  margin-right: auto;
  margin-left: auto;
}

.text-center {
  text-align: center;
}

body {
  font-family: "Helvetica", "Arial", sans-serif;
  line-height: 1;
}

.shadowed {
  text-shadow: 0px 0px 2px rgba(255, 255, 255, 0.5);
  filter: dropshadow(color=rgba(255, 255, 255, 0.5), offx=2, offy=2);
}

.main-panel {
  height: 137px;
}

.current-temp {
  font-family: "Helvetica", sans-serif;
  font-weight: bold;
  font-size: 100px;
}

.powered {
 position: absolute;
 left: 0;
 bottom: 8px;
 text-align: center;
 width: 100%;
 }

.container-row {
  height: 180px;
  padding-top: 40px;
  position: relative;
  margin-bottom: 20px;
}

.big-container {
  float: left;
  width: 50%;
}

.big-day {
  font-size: 30px;
  font-style: italic;
  font-weight: lighter;
}

.big-icon {
  font-size: 90px;
}

.big-temp {
  padding: 5px 0 0px 0;
  font-size: 25px;
}

.big-temp sup {
 font-size: 15px;
 }

.description {
  padding: 20px 0 0px 0;
  font-size: 45px;
}

.details-box {
  display: table;
  width: 100%;
}
