css:
#content { 
  height: inherit; 
}

.shadowed {
text-shadow: 0px 0px 2px rgba(0, 0, 0, 0.4);
    filter: dropshadow(color=rgba(0, 0, 0, 0.4), offx=2, offy=2);
}

.bg-div {
    font-family: 'Roboto', sans-serif;
    background-repeat: no-repeat;
    background-size: cover;
    background-position: left;
    width: 1920px !important;
    height: 1080px !important;
}

.bg-cloudy {
  background-image: url('[cloudy-image]');
}

.bg-partly-cloudy-day {
  background-image: url('[day-cloudy-image]');
}

.bg-clear-day {
  background-image: url('[day-sunny-image]');
}

.bg-fog {
  background-image: url('[fog-image]');
}

.bg-sleet {
  background-image: url('[hail-image]');
}

.bg-clear-night {
  background-image: url('[night-clear-image]');
}

.bg-partly-cloudy-night {
  background-image: url('[night-partly-cloudy-image]');
}

.bg-rain {
  background-image: url('[rain-image]');
}

.bg-snow {
  background-image: url('[snow-image]');
}

.bg-wind {
  background-image: url('[windy-image]');
}
.bg-footer {
    position: relative;
    right: 0;
    top: 106px;
    left: 0;
    text-align: center;
    background-color: #000;
    opacity: 0.9;
    padding: 30px 0 0 0;
}

.date {
    font-family: 'Roboto', sans-serif;
    font-size: 100px;
    color: #fff;
    font-weight: 900;
    margin-left: 40px;
    text-transform: uppercase;
}

.temp {
    font-family: 'Roboto', sans-serif;
    font-size: 260px;
    color: #fff;
    font-weight: 900;
}

.city {
    font-family: 'Roboto', sans-serif;
    font-size: 40px;
    color: #fff;
    margin-left: 40px;
}

.day-details {
    font-size: 40px;
    font-weight: 200;
    color: #fff;
    font-style: italic;
}

.temp-details {
    font-size: 35px;
    color: #fff;
    padding-bottom: 10px;
}

.forecast-current {
    font-weight: 900;
    color: #fff;
    font-size: 60px;
}

.forecast-second {
    margin-top: 10px;
    font-weight: 200;
    color: #fff;
    font-size: 40px;
}

.main-icon {
    color: #fff;
    font-size: 180px;
}

.second-icon {
    color: #fff;
    font-size: 60px;
    padding: 25px 0px;
}

.powered {
    padding: 15px 0;
    color: #000;
    font-size: 24px;
    background-color: white;
}

.container-fluid {
    padding-right: 15px;
    padding-left: 15px;
    margin-right: auto;
    margin-left: auto;
}

.text-right {
    text-align: right;
}

.pull-right {
    float: right!important;
}

.row {
    margin-right: -15px;
    margin-left: -15px;
}

.col-2 {
    width: 16.66666667%;
}

.col-4 {
    width: 33.33333333%;
}

.col-5 {
    width: 41.66666667%;
}

.col-6 {
    width: 50%;
}

.col-12 {
    width: 100%;
}

.weather-col {
    float: left;
    position: relative;
    min-height: 1px;
    padding-right: 15px;
    padding-left: 15px;
}
