css:
.container {
  width: 1920px !important;
  height: 1080px !important;
}

h1 {
  margin: 0.67em 0;
  font-size: 2em;
}

.container-fluid:after, .container-fluid:before, .row:after, .row:before {
  display: table;
  content: " ";
}

.container-fluid:after, .row:after {
  clear: both;
}

* {
  -webkit-box-sizing: border-box;
  -moz-box-sizing: border-box;
  box-sizing: border-box;
}

:after, :before {
  -webkit-box-sizing: border-box;
  -moz-box-sizing: border-box;
  box-sizing: border-box;
}

.text-center {
  text-align: center;
}

#content {
 color: white;

 height: inherit;
}

.shadowed {
  text-shadow: 0px 0px 2px rgba(0, 0, 0, 0.4);
  filter: dropshadow(color=rgba(0, 0, 0, 0.4), offx=2, offy=2);
}

.bg-div {
  background-repeat: no-repeat;
  background-size: cover;
  background-position: left;
}

.main-container {
 height: 100%;
 }
.bg-footer {
  position: absolute;
  right: 0;
  bottom: 0;
 width: 100%;
  left: 0;
  text-align: center;
  background-color: #000;
  opacity: 0.8;
}

.date {
  font-size: 100px;
  font-weight: 900;
  margin-left: 40px;
  text-transform: uppercase;
}

.temp {
  font-size: 260px;
  font-weight: 900;
}

.city {
  font-size: 40px;
  margin-left: 40px;
}

.day-details {
  font-size: 40px;
  font-weight: 200;
  font-style: italic;
}

.temp-details {
  font-size: 35px;
  padding-bottom: 10px;
}

.forecast-current {
  font-weight: 900;
  font-size: 60px;
}

.forecast-second {
  margin-top: 10px;
  font-weight: 200;
  font-size: 40px;
}

.main-icon {
  font-size: 180px;
}

.second-icon {
  font-size: 60px;
  padding: 25px 0px;
}

.powered {
  padding: 15px 0;
  color: #000;
  font-size: 24px;
  background-color: white;
  background-color: rgba(255, 255, 255, 0.787);
}

.text-right {
  text-align: right;
}

.pull-right {
  float: right !important;
}

.col-2 {
  width: 16.66666667%;
}

.col-4 {
  width: 33.33333333%;
}

.col-5 {
  width: 41.66666667%;
}

.col-6 {
  width: 50%;
}

.col-12 {
  width: 100%;
}

.weather-col {
  float: left;
  position: relative;
  min-height: 1px;
  padding: 20px 15px;
}
