css:
body {
    width: 540px !important;
    height: 960px !important;
}

h1 {
  margin: 0.67em 0;
  font-size: 2em;
}

.container:after, .container:before, .row:after, .row:before {
  display: table;
  content: " ";
}

.container:after, .row:after {
  clear: both;
}

#content {
  color: white;
}

.bg-footer {
  color: #333;
}

* {
  -webkit-box-sizing: border-box;
  -moz-box-sizing: border-box;
  box-sizing: border-box;
}

:after, :before {
  -webkit-box-sizing: border-box;
  -moz-box-sizing: border-box;
  box-sizing: border-box;
}

body {
  font-family: "Helvetica", "Arial", sans-serif;
  line-height: 1;
}

.container {
  background-repeat: no-repeat;
  background-size: cover;
  background-position: center;
}

.main-panel {
  margin-top: 74px;
  height: 137px;
}

.big-icon {
  font-size: 200px;
}

.date {
  font-size: 18px;
  font-family: "Helvetica", sans-serif;
  font-weight: light;
  padding-bottom: 1px;
}

.month {
  font-size: 15px;
  font-family: "Helvetica", sans-serif;
  font-weight: light;
}

.current-temp {
  font-family: "Helvetica", sans-serif;
  font-weight: bold;
  font-size: 100px;
}

.main-cont {
  height: 780px;
  padding: 0px 50px;
  position: relative;
  margin-right: auto;
  margin-left: auto;
}

.main-icon {
  font-size: 70px;
}

.summary {
  font-weight: bold;
  font-size: 80px;
  padding-top: 30px;
}

.powered {
  font-weight: light;
  font-size: 15px;
  padding-bottom: 40px;
}

.small-day {
  padding-top: 50px;
  font-size: 28px;
  font-style: italic;
  font-weight: lighter;
}

.small-icon {
  font-size: 180px;
  padding: 90px 0px;
}

.big-temp {
  padding-top: 30px;
  font-size: 120px;
  font-weight: bold;
}

.big-temp sup {
 font-size: 70px;
 }

.details-box {
  height: 290px;
  padding-left: 30px;
}

.upcoming-days {
  height: 365px;
  text-align: center;
}

.selector-day {
  font-size: 15px;
  font-weight: light;
  margin-top: 25px;
}

.selector-temp {
  font-size: 25px;
  font-weight: bold;
  font-style: italic;
}

.arrow-down {
  width: 0;
  height: 0;
  border-left: 20px solid transparent;
  border-right: 20px solid transparent;
  border-top: 20px solid rgba(0, 0, 0, 0.8);
  margin-left: 120px;
}

.row-details {
  background-color: #fff;
  height: 180px;
  margin-left: -15px;
  margin-right: -15px;
}

.row-day {
  padding: 30px 0px;
}

.day {
  font-size: 30px;
  font-weight: bold;
}

.temp {
  font-size: 30px;
  font-weight: light;
}

.icon-up {
  font-size: 30px;
}

.shadowed {
  text-shadow: 0px 0px 2px #111;
  filter: dropshadow(color=#111, offx=2, offy=2);
}

.container, .container {
  padding-right: 15px;
  padding-left: 15px;
  margin-right: auto;
  margin-left: auto;
}

.text-right {
  text-align: right;
}

.pull-right {
  float: right !important;
}

.col-2 {
  width: 16.66666667%;
}

.col-3 {
  width: 25%;
}

.col-4 {
  width: 33.33333333%;
}

.col-5 {
  width: 41.66666667%;
}

.col-6 {
  width: 50%;
}

.col-12 {
  width: 100%;
}

.weather-col {
  text-align: center;
  float: left;
  position: relative;
  min-height: 1px;
  padding-right: 15px;
  padding-left: 15px;
}
