css:
body {
 width: 1920px !important;
 height: 1080px !important;
 }

#content {
    color: #fff;
}

#content, .main-container {
 width: 100%;
 height: 100%;
}

.shadowed {
text-shadow: 0px 0px 2px rgba(0, 0, 0, 0.4);
    filter: dropshadow(color=rgba(0, 0, 0, 0.4), offx=2, offy=2);
}

.corner-info {
    width: 200px;
    background-color: #000;
    background-color: #0008;
    opacity: 0.6;
    border-radius: 0 0 30px 0;
    padding: 8px;
}

.block-container {
    display: inline-block;
    width: 50%;
    height: 50px;
    vertical-align: top;
}

.date {
      font-weight: 900;
    font-size: 20px;
    text-transform: uppercase;
}

.temp {
      font-size: 30px;
    font-weight: 900;
}

.main-icon {
 padding: 8px;
}

.main-icon i {
    font-size: 50px;
    padding: 5px;
}

.powered {
  position: absolute;
 bottom: 0;
 width: 100%;
 padding: 4px 0;
  color: #000;
  font-size: 13px;
 text-align: center;
  background-color: white;
  background-color: rgba(255, 255, 255, 0.787);
}
