css:
body {
    font-family: "Helvetica", "Arial", sans-serif;
    line-height: 1;
    color: #fff;
}

.container {
    width: 960px !important;
    height: 493px !important;
}

.contenedor {
    height: 300px;
    background-color: rgba(0, 0, 0, 0.56);
    padding-top: 45px;
}

.upcoming {
    height: 193px;
    background-color: rgba(0, 0, 0, 0.56);
    border-top: 2px solid #fff;
}

.city {
    font-size: 50px;
    font-weight: bold;
}

.country {
    font-size: 25px;
}

.temp {
    font-size: 120px;
    font-weight: bold;
}

.feels {
    font-size: 30px;
}

.main-icon {
    font-size: 200px;
}

.day-title {
    font-style: italic;
    font-size: 30px;
    font-weight: lighter;
}

.icon-weather {
    padding: 25px 0px;
    font-size: 35px;
}

.temp-small {
    font-size: 18px;
}

.top-small {
    padding-top: 30px;
}

.border {
    border-right: 2px solid #fff;
}

.rightnow {
    height: 123.25px;
    padding-top: 20px;
    background-color: rgba(0, 0, 0, 0.56);
}

.now {
    font-size: 25px;
}

.current-wea {
    font-size: 22px;
    padding: 10px 0px;
}

.extra-info {
    font-size: 18px;
}

.small-border {
    border-bottom: 2px solid #fff;
}
