css:
body {
 width: 970px !important;
 height: 493px !important;
 }

.text-center {
 text-align: center;
}
 h1 {
 margin: 0.67em 0;
 font-size: 2em;
}

.container-fluid:after, .container-fluid:before, .row:after, .row:before {
    display: table;
    content: " ";
}
.container-fluid:after, .row:after {
    clear: both;
}

* {
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
}

:after,:before {
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
}

body {
    font-family: "Helvetica", "Arial", sans-serif;
    line-height: 1;
    color: #fff;
}

.container {
    width: 960px !important;
    height: 493px !important;
}

.contenedor {
    height: 300px;
    background-color: rgba(0, 0, 0, 0.56);
    padding: 70px 20px 0 20px;
}

.upcoming {
    height: 193px;
    background-color: rgba(0, 0, 0, 0.56);
    border-top: 2px solid #fff;
}

.city {
    font-size: 50px;
    font-weight: bold;
}

.country {
    font-size: 25px;
}

.temp {
    font-size: 120px;
    font-weight: bold;
}

.temp sup {
 font-size: 50px;
 }

.feels {
    font-size: 30px;
}

.main-icon {
    font-size: 200px;
}

.day-title {
    font-style: italic;
    font-size: 30px;
    font-weight: lighter;
}

.icon-weather {
    padding: 25px 0px;
    font-size: 35px;
}

.temp-small {
    font-size: 18px;
}

.top-small {
    padding-top: 30px;
}

.border {
    border-right: 2px solid #fff;
}

.rightnow {
    height: 123.25px;
    padding-top: 20px;
    background-color: rgba(0, 0, 0, 0.56);
}

.now {
    font-size: 25px;
}

.current-wea {
    font-size: 22px;
    padding: 10px 0px;
}

.extra-info {
    font-size: 18px;
}

.small-border {
    border-bottom: 2px solid #fff;
}

.container-fluid, .container {
    padding-right: 15px;
    padding-left: 15px;
    margin-right: auto;
    margin-left: auto;
}

.text-right {
    text-align: right;
}

.pull-right {
    float: right!important;
}

.row {
    margin-right: -15px;
    margin-left: -15px;
}

.col-2 {
    width: 16.66666667%;
}

.col-3 {
    width: 25%;
}

.col-4 {
    width: 33.33333333%;
}

.col-5 {
    width: 41.66666667%;
}

.col-6 {
    width: 50%;
}

.col-9 {
    width: 75%;
}

.col-12 {
    width: 100%;
}

.weather-col {
    float: left;
    position: relative;
    min-height: 1px;
    padding-right: 15px;
    padding-left: 15px;
}
