css:
body {
 width: 540px !important;
 height: 540px !important;
 }

#content {
 color: #fff;
}

.center-block {
    display: block;
    margin-right: auto;
    margin-left: auto;
}

.container {
    padding-right: 15px;
    padding-left: 15px;
    margin-right: auto;
    margin-left: auto;
}

.text-center {
 text-align: center;
}
 h1 {
 margin: 0.67em 0;
 font-size: 2em;
}

.container-fluid:after, .container-fluid:before, .row:after, .row:before {
    display: table;
    content: " ";
}
.container-fluid:after, .row:after {
    clear: both;
}

* {
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
}

:after,:before {
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
}

 body {
    font-family: "Helvetica", "Arial", sans-serif;
    line-height: 1;
}

.container {
    background-repeat: no-repeat;
    background-size: cover;
    background-position: center;
    width: 540px !important;
    height: 540px !important;
}

.main-panel {
    margin-top: 74px;
    height: 137px;
}

.big-icon {
    font-size: 200px;
}

.date {
    font-size: 18px;
    font-family: "Helvetica", sans-serif;
    font-weight: light;
    padding-bottom: 1px;
}

.month {
    font-size: 15px;
    font-family: "Helvetica", sans-serif;
    font-weight: light;
}

.current-temp {
    font-family: "Helvetica", sans-serif;
    font-weight: bold;
    font-size: 100px;
}

.main-cont {
    width: 400px;
    padding: 90px 50px;
    position: relative;
}

.main-icon {
    font-size: 70px;
}

.summary {
    font-weight: bold;
    font-size: 30px;
    padding-top: 20px;
}

.powered {
    font-weight: light;
    font-size: 15px;
    padding-bottom: 40px;
}

.small-day {
    padding-top: 50px;
    font-size: 28px;
    font-style: italic;
    font-weight: lighter;
}

.small-icon {
    font-size: 130px;
    padding: 20px 0px 40px 0px;
}

.big-temp {
    padding-top: 20px;
    font-size: 120px;
    font-weight: bold;
}

.big-temp sup {
 font-size: 50px;
 }

.even {
    background-color: rgba(0, 0, 0, 0.6);
    background: rgba(0, 0, 0, 0.6);
}

.odd {
    background-color: rgba(0, 0, 0, 0.8);
    background: rgba(0, 0, 0, 0.8);
    color: #fff !important;
}

.details-box {
    height: 290px;
    padding-left: 30px;
    color: #272625;
}

.upcoming-days {
    height: 365px;
    text-align: center;
}

.selector-day {
    font-size: 15px;
    font-weight: light;
    margin-top: 25px;
}

.selector-temp {
    font-size: 25px;
    font-weight: bold;
    font-style: italic;
}

.arrow-down {
    width: 0;
    height: 0;
    border-left: 20px solid transparent;
    border-right: 20px solid transparent;
    border-top: 20px solid rgba(0, 0, 0, 0.8);
    margin-left: 120px;
}

.row-details {
    margin-top: 128px;
    background-color: #fff;
    height: 182px;
}

.row-day {
    padding: 30px 0px;
    border-bottom: #eee solid 1px;
    color: #000;
}

.day {
    font-size: 30px;
    font-weight: bold;
    color: #434343;
}

.temp {
    font-size: 30px;
    font-weight: light;
    color: #434343;
}

.icon-up {
    font-size: 30px;
    color: color: #434343;
}

.row-time {
    padding: 20px 0px 10px 0px;
    font-weight: bold;
    font-size: 20px;
}

.shadowed {
    text-shadow: 0px 0px 2px #111;
    filter: dropshadow(color=#111, offx=2, offy=2);
}


}
