css:
body {
    font-family: "Helvetica", "Arial", sans-serif;
    line-height: 1;
}

.container {
    background-repeat: no-repeat;
    background-size: cover;
    background-position: center;
    width: 970px !important;
    height: 700px !important;
    text-shadow: 0px 0px 2px rgba(0, 0, 0, 0.4);
    filter: dropshadow(color=rgba(0, 0, 0, 0.4), offx=2, offy=2);
}

.main-panel {
    margin-top: 100px;
}

.date {
    text-transform: uppercase;
    color: #fff;
    font-size: 50px;
    font-family: "Helvetica", sans-serif;
    font-weight: bold;
    padding-bottom: 5px;
}

.month {
    font-size: 40px;
    color: #fff;
    font-family: "Helvetica", sans-serif;
    font-weight: bold;
}

.current-temp {
    font-family: "Helvetica", sans-serif;
    font-weight: bold;
    font-size: 130px;
    color: #fff;
}

.footer {
    margin-top: 155px;
    height: 220px;
    color: #fff;
    text-align: center;
    padding: 30px 0px;
    position: relative;
}

.main-icon {
    font-size: 200px;
    color: #fff;
}

.small-day {
    font-size: 30px;
    font-style: italic;
    font-weight: light;
}

.small-icon {
    font-size: 60px;
    padding: 30px;
}

.small-temp {
    font-size: 20px;
}

.powered {
    background-color: #fff;
    height: 20px;
    position: relative;
}

.bg-cloudy {
    background-image: url('[cloudy-image]');
}

.bg-partly-cloudy-day {
    background-image: url('[day-cloudy-image]');
}

.bg-clear-day {
    background-image: url('[day-sunny-image]');
}

.bg-fog {
    background-image: url('[fog-image]');
}

.bg-sleet {
    background-image: url('[hail-image]');
}

.bg-clear-night {
    background-image: url('[night-clear-image]');
}

.bg-partly-cloudy-night {
    background-image: url('[night-partly-cloudy-image]');
}

.bg-rain {
    background-image: url('[rain-image]');
}

.bg-snow {
    background-image: url('[snow-image]');
}

.bg-wind {
    background-image: url('[windy-image]');
}
