css:
body {
 width: 540px !important;
 height: 960px !important;
 }

#content {
 color: #f9f9f9;
 }

.center-block {
    display: block;
    margin-right: auto;
    margin-left: auto;
}

.container {
    padding-right: 15px;
    padding-left: 15px;
    margin-right: auto;
    margin-left: auto;
}

.text-center {
 text-align: center;
}
 h1 {
 margin: 0.67em 0;
 font-size: 2em;
}

.container:after, .container:before, .row:after, .row:before {
    display: table;
    content: " ";
}
.container:after, .row:after {
    clear: both;
}

* {
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
}

:after,:before {
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
}

 body {
    font-family: "Helvetica", "Arial", sans-serif;
    line-height: 1;
}

.container {
    background-repeat: no-repeat;
    background-size: cover;
    background-position: center;
    width: 540px !important;
    height: 960px !important;
}

.main-panel {
    margin-top: 74px;
    height: 137px;
}

.big-icon {
    font-size: 200px;
}

.date {
    font-size: 18px;
    font-family: "Helvetica", sans-serif;
    font-weight: light;
    padding-bottom: 1px;
}

.month {
    font-size: 15px;
    font-family: "Helvetica", sans-serif;
    font-weight: light;
}

.current-temp {
    font-family: "Helvetica", sans-serif;
    font-weight: bold;
    font-size: 100px;
}

.main-cont {
    margin-top: 50px;
    width: 500px;
    height: 380px;
    padding: 0px 40px;
    position: relative;
}

.main-icon {
    font-size: 70px;
}

.city {
    font-weight: bold;
    font-size: 40px;
    padding-top: 30px;
}

.country {
    font-weight: light;
    font-size: 15px;
    padding-bottom: 10px;
}

.small-day {
    padding-top: 50px;
    font-size: 28px;
    font-style: italic;
    font-weight: lighter;
}

.small-icon {
    font-size: 120px;
    padding: 35px 0px;
}

.big-temp {
    font-size: 90px;
    font-weight: bold;
    float: right;
}

.big-temp sup {
 font-size: 40px;
 }

.even {
    background-color: rgba(0, 0, 0, 0.6);
    background: rgba(0, 0, 0, 0.6);
}

.odd {
    background-color: rgba(0, 0, 0, 0.8);
    background: rgba(0, 0, 0, 0.8);
    color: #fff !important;
}

.details-box {
    height: 290px;
    padding-left: 30px;
    padding-top: 40px;
    color: #272625;
}

.upcoming-days {
    height: 365px;
    text-align: center;
    padding-top: 10px;
}

.selector-day {
    font-size: 15px;
    font-weight: light;
    margin-top: 25px;
}

.selector-temp {
    font-size: 25px;
    font-weight: bold;
    font-style: italic;
}

.arrow-down {
    width: 0;
    height: 0;
    border-left: 20px solid transparent;
    border-right: 20px solid transparent;
    border-top: 20px solid rgba(0, 0, 0, 0.8);
    margin-left: 120px;
}

.row-details {
    margin-top: 145px;
    height: 360px;
}

.row-day {
    padding: 30px 0px;
}

.day {
    font-size: 25px;
    font-weight: lighter;
    font-style: italic;
    text-align: left;
text-transform: uppercase;
}

.temp {
    font-size: 25px;
    font-weight: light;
}

.icon-up {
    font-size: 30px;
    color: color: #fff;
}

.summary {
    font-weight: bold;
    font-size: 20px;
    padding-top: 10px;
}

.powered {
    font-weight: light;
    font-size: 15px;
    padding-bottom: 40px;
}

.shadowed {
    text-shadow: 0px 0px 2px #111;
    filter: dropshadow(color=#111, offx=2, offy=2);
}

.container-fluid, .container {
    padding-right: 15px;
    padding-left: 15px;
    margin-right: auto;
    margin-left: auto;
}

.text-right {
    text-align: right;
}

.pull-right {
    float: right!important;
}

.row {
    margin-right: -15px;
    margin-left: -15px;
}

.offset-xs-1 {
    margin-left: 8.33333333%;
}

.col-2 {
    width: 16.66666667%;
}

.col-3 {
    width: 25%;
}

.col-4 {
    width: 33.33333333%;
}

.col-5 {
    width: 41.66666667%;
}

.col-6 {
    width: 50%;
}

.col-7 {
    width: 58.33333333%;
}

.col-9 {
    width: 75%;
}

.col-12 {
    width: 100%;
}

.weather-col {
    float: left;
    position: relative;
    min-height: 1px;
    padding-right: 15px;
    padding-left: 15px;
}
