css:
body {
  width: 960px !important;
  height: 540px !important;
}

p {
  margin: 0 0 10px;
}

.h1, .h2, .h3, h1, h2, h3 {
  margin-top: 20px;
  margin-bottom: 10px;
}

.h3, h3 {
  font-size: 24px;
}

.center-block {
  display: block;
  margin-right: auto;
  margin-left: auto;
}

#content {
 color: white;
 }

.text-center {
  text-align: center;
}

h1 {
  margin: 0.67em 0;
  font-size: 2em;
}

.container:after, .container:before, .row:after, .row:before {
  display: table;
  content: " ";
}

.container:after, .row:after {
  clear: both;
}

* {
  -webkit-box-sizing: border-box;
  -moz-box-sizing: border-box;
  box-sizing: border-box;
}

:after, :before {
  -webkit-box-sizing: border-box;
  -moz-box-sizing: border-box;
  box-sizing: border-box;
}

body {
  font-family: "Helvetica", "Arial", sans-serif;
  line-height: 1;
}

.container {
  background-repeat: no-repeat;
  background-size: cover;
  background-position: center;
  width: 960px !important;
  height: 540px !important;
}

.main-panel {
  margin-top: 74px;
  height: 137px;
}

.big-icon {
  font-size: 120px;

  margin-right: 20px;
}

.date {

  font-size: 18px;
  font-family: "Helvetica", sans-serif;
  font-weight: light;
  padding-bottom: 1px;
}

.month {
  font-size: 15px;

  font-family: "Helvetica", sans-serif;
  font-weight: light;
}

.current-temp {
  font-family: "Helvetica", sans-serif;
  font-weight: bold;
  font-size: 100px;

}

.current-temp sup {
 font-size: 50px;
 }

.footer {
  height: 290px;

  padding: 30px 30px;
  position: relative;
  margin-bottom: 20px;
}

.main-icon {
  font-size: 70px;

}

.small-day {
  padding-top: 54px;
  font-size: 20px;
  font-style: italic;
  font-weight: lighter;
  text-transform: uppercase;
}

.small-icon {
  font-size: 30px;
  padding: 20px;
  color: #272625;
}

.small-temp {
  font-size: 16px;

}

.powered {
  font-weight: bold;
  font-size: 1.4em;
  padding-top: 5px;
}

.upcoming-days:nth-child(odd) {
  background-color: rgba(255, 255, 255, 0.6);
  background: rgba(255, 255, 255, 0.6);

}

.main-detail, .upcoming-days:nth-child(even) {
  background-color: rgba(255, 255, 255, 0.9);
  background: rgba(255, 255, 255, 0.9);
}

.details-box {
  height: 290px;
  padding-left: 30px !important;

}

.upcoming-days {
  height: 195px;
  margin-top: 95px !important;
  text-align: center;
}

.selector-day {
  font-size: 15px;
  font-weight: light;
  margin-top: 25px;
}

.selector-temp {
  font-size: 25px;
  font-weight: bold;
  font-style: italic;
}

.shadowed {
  text-shadow: 0px 0px 4px rgba(0, 0, 0, 0.4);
  filter: dropshadow(color=rgba(0, 0, 0, 0.4), offx=2, offy=2);
}

.container-fluid, .container {
  margin-right: auto;
  margin-left: auto;
}

.text-right {
  text-align: right;
}

.pull-right {
  float: right !important;
}

.offset-xs-1 {
  margin-left: 8.33333333%;
}

.col-2 {
  width: 16.66666667%;
}

.col-3 {
  width: 25%;
}

.col-4 {
  width: 33.33333333%;
}

.col-5 {
  width: 41.66666667%;
}

.col-6 {
  width: 50%;
}

.col-7 {
  width: 58.33333333%;
}

.col-9 {
  width: 75%;
}

.col-12 {
  width: 100%;
}

.weather-col {
  float: left;
  position: relative;
  min-height: 1px;
}

.offset-xs-6 {
  margin-left: 50%;
}

.bg-footer{
   color: #272625;
}
