css:
body {
    font-family: "Helvetica", "Arial", sans-serif;
    line-height: 1;
}

.container {
    background-repeat: no-repeat;
    background-size: cover;
    background-position: center;
    width: 960px !important;
    height: 540px !important;
}

.main-panel {
    margin-top: 74px;
    height: 137px;
}

.big-icon {
    font-size: 120px;
    color: #fff;    
    margin-right: 20px;
}

.date {
    color: #fff;
    font-size: 18px;
    font-family: "Helvetica", sans-serif;
    font-weight: light;
    padding-bottom: 1px;
}

.month {
    font-size: 15px;
    color: #fff;
    font-family: "Helvetica", sans-serif;
    font-weight: light;
}

.current-temp {
    font-family: "Helvetica", sans-serif;
    font-weight: bold;
    font-size: 100px;
    color: #fff;
}

.footer {
    height: 290px;
    color: #fff;
    padding: 30px 30px;
    position: relative;
    margin-bottom: 20px;
}

.main-icon {
    font-size: 70px;
    color: #fff;
}

.small-day {
    padding-top: 54px;
    font-size: 20px;
    font-style: italic;
    font-weight: lighter;
    color: #272625;
		text-transform: uppercase;
}

.small-icon {
    font-size: 30px;
    padding: 20px;
    color: #272625;
}

.small-temp {
    font-size: 16px;
    color: #272625;
}

.powered {
    font-weight: bold;
    font-size: 1.4em;
    padding-top: 5px;
}

.upcoming-days:nth-child(odd) {
    background-color: rgba(255, 255, 255, 0.6);
    background: rgba(255, 255, 255, 0.6);
    color: rgba(255, 255, 255, 0.6);
}

.odd, .upcoming-days:nth-child(even) {
    background-color: rgba(255, 255, 255, 0.9);
    background: rgba(255, 255, 255, 0.9);
    color: rgba(255, 255, 255, 0.9);
}

.details-box {
    height: 290px;
    padding-left: 30px;
    color: #272625;
}

.upcoming-days {
    height: 195px;
    margin-top: 95px !important;
    text-align: center;
}

.selector-day {
    font-size: 15px;
    font-weight: light;
    margin-top: 25px;
}

.selector-temp {
    font-size: 25px;
    font-weight: bold;
    font-style: italic;
}

.shadowed {
    text-shadow: 0px 0px 4px rgba(0, 0, 0, 0.4);
    filter: dropshadow(color=rgba(0, 0, 0, 0.4), offx=2, offy=2);
}

.bg-cloudy {
    background-image: url('[cloudy-image]');
}

.bg-partly-cloudy-day {
    background-image: url('[day-cloudy-image]');
}

.bg-clear-day {
    background-image: url('[day-sunny-image]');
}

.bg-fog {
    background-image: url('[fog-image]');
}

.bg-sleet {
    background-image: url('[hail-image]');
}

.bg-clear-night {
    background-image: url('[night-clear-image]');
}

.bg-partly-cloudy-night {
    background-image: url('[night-partly-cloudy-image]');
}

.bg-rain {
    background-image: url('[rain-image]');
}

.bg-snow {
    background-image: url('[snow-image]');
}

.bg-wind {
    background-image: url('[windy-image]');
}
