css:
body {
  width: 540px !important;
  height: 960px !important;
  font-family: "Helvetica", "Arial", sans-serif;
  line-height: 1;
}

.center-block {
  display: block;
  margin-right: auto;
  margin-left: auto;
}

#content {
  color: white;
}

.container {
  margin-right: auto;
  margin-left: auto;
}

.text-center {
  text-align: center;
}

h1 {
  margin: 0.67em 0;
  font-size: 2em;
}

.container:after, .container:before, .row:after, .row:before {
  display: table;
  content: " ";
}

.container:after, .row:after {
  clear: both;
}

* {
  -webkit-box-sizing: border-box;
  -moz-box-sizing: border-box;
  box-sizing: border-box;
}

:after, :before {
  -webkit-box-sizing: border-box;
  -moz-box-sizing: border-box;
  box-sizing: border-box;
}

.container {
  background-repeat: no-repeat;
  background-size: cover;
  background-position: center;
  width: 540px !important;
  height: 960px !important;
}

.main-panel {
  margin-top: 74px;
  height: 137px;
}

.big-icon {
  font-size: 200px;
  }

.date {
  font-size: 18px;
  font-family: "Helvetica", sans-serif;
  font-weight: light;
  padding-bottom: 1px;
}

.month {
  font-size: 15px;
  font-family: "Helvetica", sans-serif;
  font-weight: light;
}

.current-temp {
  font-family: "Helvetica", sans-serif;
  font-weight: bold;
  font-size: 100px;
}

.main-cont {
  width: 400px;
  height: 400px;
  margin: 0 auto !important;
  position: relative;
}

.main-icon {
  font-size: 70px;
}

.city {
  font-weight: bold;
  font-size: 40px;
  padding-top: 30px;
}

.country {
  font-weight: light;
  font-size: 15px;
  padding-bottom: 30px;
}

.small-day {
  padding-top: 50px;
  font-size: 28px;
  font-style: italic;
  font-weight: lighter;
  }

.small-icon {
  font-size: 120px;
  padding: 35px 0px;
  }

.big-temp {
  font-size: 90px;
    font-weight: bold;
}

.big-temp sup {
 font-size: 50px;
 }

.even {
  background-color: rgba(255, 255, 255, 0.8);
  background: rgba(255, 255, 255, 0.8);
  }

.bg-footer {
 color: #333;
 }

.odd {
  background-color: rgba(255, 255, 255, 0.6);
  background: rgba(255, 255, 255, 0.6);
}

.details-box {
  height: 290px;
  padding-left: 30px;
  color: #272625;
}

.upcoming-days {
  height: 365px;
  text-align: center;
    margin-top: 50px;
}

.selector-day {
  font-size: 15px;
  font-weight: light;
  margin-top: 25px;
  }

.selector-temp {
  font-size: 25px;
  font-weight: bold;
  font-style: italic;
}

.row-details {
  margin-top: 280px;
  height: 230px;
    padding: 0px 30px;
}

.row-day {
  padding: 30px 0px;
}

.day {
  font-size: 25px;
  font-weight: lighter;

  font-style: italic;
  text-transform: uppercase;
}

.temp {
  font-size: 25px;
  font-weight: light;

}

.icon-up {
  font-size: 50px;

  padding: 40px;
}

.circle {
  border-radius: 50%;
  width: 380px;
  height: 380px;
  background-color: rgba(0, 0, 0, 0.5);
}

.summary {
  font-weight: bold;
  font-size: 20px;
}

.powered {
  background-color: #444;
 background-color: rgba(0, 0, 0, 0.63);
   color: #fff;
  font-size: 18px;
  padding: 8px;
 line-height: 18px;
 n}

.shadowed {
  text-shadow: 0px 0px 2px #111;
  filter: dropshadow(color=#111, offx=2, offy=2);
}

.container-fluid, .container {
  margin-right: auto;
  margin-left: auto;
}

.text-right {
  text-align: right;
}

.pull-right {
  float: right !important;
}

.offset-xs-1 {
  margin-left: 8.33333333%;
}

.col-2 {
  width: 16.66666667%;
}

.col-3 {
  width: 25%;
}

.col-4 {
  width: 33.33333333%;
}

.col-5 {
  width: 41.66666667%;
}

.col-6 {
  width: 50%;
}

.col-7 {
  width: 58.33333333%;
}

.col-9 {
  width: 75%;
}

.col-12 {
  width: 100%;
}

.weather-col {
  float: left;
  position: relative;
  min-height: 1px;
  padding-right: 10px;
  padding-left: 10px;
}
