css:
body {
    font-family: "Helvetica", "Arial", sans-serif;
    line-height: 1;
}

.container {
    background-repeat: no-repeat;
    background-size: cover;
    background-position: center;
    width: 540px !important;
    height: 960px !important;
}

.main-panel {
    margin-top: 74px;
    height: 137px;
}

.big-icon {
    font-size: 200px;
    color: #fff;
}

.date {
    color: #fff;
    font-size: 18px;
    font-family: "Helvetica", sans-serif;
    font-weight: light;
    padding-bottom: 1px;
}

.month {
    font-size: 15px;
    color: #fff;
    font-family: "Helvetica", sans-serif;
    font-weight: light;
}

.current-temp {
    font-family: "Helvetica", sans-serif;
    font-weight: bold;
    font-size: 100px;
    color: #fff;
}

.main-cont {
    width: 500px;
    height: 380px;
    color: #fff;
    padding: 0px 50px;
    position: relative;
}

.main-icon {
    font-size: 70px;
    color: #fff;
}

.city {
    font-weight: bold;
    font-size: 40px;
    padding-top: 30px;
}

.country {
    font-weight: light;
    font-size: 15px;
    padding-bottom: 30px;
}

.small-day {
    padding-top: 50px;
    font-size: 28px;
    font-style: italic;
    font-weight: lighter;
    color: #fff;
}

.small-icon {
    font-size: 120px;
    padding: 35px 0px;
    color: #fff;
}

.big-temp {
    font-size: 90px;
    color: #fff;
    font-weight: bold;
}

.even {
    background-color: rgba(255, 255, 255, 0.8);
    background: rgba(255, 255, 255, 0.8);
    color: #fff;
}

.odd {
    background-color: rgba(255, 255, 255, 0.6);
    background: rgba(255, 255, 255, 0.6);
    color: #fff !important;
}

.details-box {
    height: 290px;
    padding-left: 30px;
    color: #272625;
}

.upcoming-days {
    height: 365px;
    text-align: center;
    color: #fff;
    padding-top: 50px;
}

.selector-day {
    font-size: 15px;
    font-weight: light;
    margin-top: 25px;
    color: #fff;
}

.selector-temp {
    font-size: 25px;
    font-weight: bold;
    font-style: italic;
}

.row-details {
    margin-top: 310px;
    height: 230px;
    color: #fff;
    padding: 0px 30px;
}

.row-day {
    padding: 30px 0px;
}

.day {
    font-size: 25px;
    font-weight: lighter;
    color: #000;
    font-style: italic;
    text-transform: uppercase;
}

.temp {
    font-size: 25px;
    font-weight: light;
    color: #000;
}

.icon-up {
    font-size: 50px;
    color: #000;
    padding: 40px;
}

.circle {
    border-radius: 50%;
    width: 350px;
    height: 350px;
    background-color: rgba(0, 0, 0, 0.5);
}

.summary {
    font-weight: bold;
    font-size: 20px;
}

.powered {
    font-weight: bold;
    font-size: 15px;
    padding: 7px 0;
}

.shadowed {
    text-shadow: 0px 0px 2px #111;
    filter: dropshadow(color=#111, offx=2, offy=2);
}

.bg-cloudy {
    background-image: url('[cloudy-image]');
}

.bg-partly-cloudy-day {
    background-image: url('[day-cloudy-image]');
}

.bg-clear-day {
    background-image: url('[day-sunny-image]');
}

.bg-fog {
    background-image: url('[fog-image]');
}

.bg-sleet {
    background-image: url('[hail-image]');
}

.bg-clear-night {
    background-image: url('[night-clear-image]');
}

.bg-partly-cloudy-night {
    background-image: url('[night-partly-cloudy-image]');
}

.bg-rain {
    background-image: url('[rain-image]');
}

.bg-snow {
    background-image: url('[snow-image]');
}

.bg-wind {
    background-image: url('[windy-image]');
}
