css:
body {
  width: 960px !important;
  height: 540px !important;
}

.center-block {
  display: block;
  margin-right: auto;
  margin-left: auto;
}

.text-center {
  text-align: center;
}

h1 {
  margin: 0.67em 0;
  font-size: 2em;
}

.container:after, .container:before, .row:after, .row:before {
  display: table;
  content: " ";
}

.container:after, .row:after {
  clear: both;
}

* {
  -webkit-box-sizing: border-box;
  -moz-box-sizing: border-box;
  box-sizing: border-box;
}

:after, :before {
  -webkit-box-sizing: border-box;
  -moz-box-sizing: border-box;
  box-sizing: border-box;
}

body {
  font-family: "Helvetica", "Arial", sans-serif;
  line-height: 1;
}

#content {
  color: #fff;
}

.container {
  background-repeat: no-repeat;
  background-size: cover;
  background-position: center;
  width: 960px !important;
  height: 540px !important;
}

.powered {
  font-weight: bold;
  font-style: italic;
}

.main-panel {
  margin-top: 74px;
  height: 137px;
}

.big-icon {
  font-size: 120px;
}

.date {
  font-size: 18px;
  font-family: "Helvetica", sans-serif;
  font-weight: light;
  padding-bottom: 1px;
}

.month {
  font-size: 15px;
  font-family: "Helvetica", sans-serif;
  font-weight: light;
}

.current-temp {
  font-family: "Helvetica", sans-serif;
  font-weight: bold;
  font-size: 100px;
}

.main-cont {
  margin-top: 72px;
  height: 870px;
  height: 365px;
  padding: 0px 50px;
  position: relative;
}

.main-icon {
  font-size: 70px;
}

.small-day {
  padding-top: 50px;
  font-size: 28px;
  font-style: italic;
  font-weight: lighter;
}

.small-icon {
  font-size: 70px;
  padding: 70px 0px;
}

.small-temp {
  font-size: 20px;
  font-weight: lighter;
  font-style: italic;
}

.even {
  background-color: rgba(0, 0, 0, 0.6);
  background: rgba(0, 0, 0, 0.6);
}

.odd {
  background-color: rgba(0, 0, 0, 0.8);
  background: rgba(0, 0, 0, 0.8);
}

.details-box {
  height: 290px;
  padding-left: 30px;
}

.upcoming-days {
  height: 365px;
  text-align: center;
}

.selector-day {
  font-size: 15px;
  font-weight: light;
  margin-top: 25px;
}

.selector-temp {
  font-size: 25px;
  font-weight: bold;
  font-style: italic;
}

.arrow-down {
  width: 0;
  height: 0;
  border-left: 20px solid transparent;
  border-right: 20px solid transparent;
  border-top: 20px solid rgba(0, 0, 0, 0.8);
  margin-left: 120px;
}

.row-details {
  padding: 15px 35px;
  background-color: rgba(0, 0, 0, 0.7);
  background: rgba(0, 0, 0, 0.7);
}

.container-fluid, .container {
  margin-right: auto;
  margin-left: auto;
}

.text-right {
  text-align: right;
}

.pull-right {
  float: right !important;
}

.offset-xs-1 {
  margin-left: 8.33333333%;
}

.col-2 {
  width: 16.66666667%;
}

.col-3 {
  width: 25%;
}

.col-4 {
  width: 33.33333333%;
}

.col-5 {
  width: 41.66666667%;
}

.col-6 {
  width: 50%;
}

.col-7 {
  width: 58.33333333%;
}

.col-9 {
  width: 75%;
}

.col-12 {
  width: 100%;
}

.weather-col {
  float: left;
  position: relative;
  min-height: 1px;
}
