css:
body {
 width: 960px !important;
 height: 180px !important;
 }

.center-block {
    display: block;
    margin-right: auto;
    margin-left: auto;
}

#content {
 color: white;
 }

.text-center {
 text-align: center;
}
 h1 {
 margin: 0.67em 0;
 font-size: 2em;
}

.container:after, .container:before, .row:after, .row:before {
    display: table;
    content: " ";
}
.container:after, .row:after {
    clear: both;
}

* {
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
}

:after,:before {
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
}

 body {
    background-color: #000;
    font-family: "Helvetica", "Arial", sans-serif;
    line-height: 1;
}

.container {
    width: 960px !important;
    height: 180px !important;
    padding-top: 30px;
}

.current-condition {
    font-weight: bold;
    font-size: 23px;
}

.info1 {
    font-size: 14px;
    padding: 20px 0px;
}

.day {
    font-size: 18px;
    font-weight: lighter;
    font-style: italic;
}

.icon {
    font-size: 50px;
    padding: 20px 0px;
}

.temp {
    font-size: 15px;
}

.powered {
    font-weight: bold;

    font-style: italic;
    padding-top: 26px;
    font-size: 1.2em;
}

.text-right {
    text-align: right;
}

.pull-right {
    float: right!important;
}

.offset-xs-1 {
    margin-left: 8.33333333%;
}

.col-2 {
    width: 16.66666667%;
}

.col-3 {
    width: 25%;
}

.col-4 {
    width: 33.33333333%;
}

.col-5 {
    width: 41.66666667%;
}

.col-6 {
    width: 50%;
}

.col-7 {
    width: 58.33333333%;
}

.col-9 {
    width: 75%;
}

.col-12 {
    width: 100%;
}

.weather-col {
    float: left;
    position: relative;
    min-height: 1px;
    padding-right: 15px;
    padding-left: 15px;
}
