css:
body {
    font-family: "Helvetica", "Arial", sans-serif;
    line-height: 1;
}

.container {
    background-color: #000;
    width: 189px !important;
    height: 900px !important;
    color: #fff;
    padding-top: 30px;
}

.current-condition {
    font-weight: bold;
    font-size: 23px;
}

.info1 {
    font-size: 14px;
    padding: 15px 0px;
}

.info2 {
    padding-bottom: 10px;
}

.day {
    font-size: 18px;
    font-weight: lighter;
    font-style: italic;
}

.icon {
    font-size: 50px;
    padding: 20px 0px;
}

.temp {
    font-size: 15px;
}

.border {
    padding-top: 40px;
    padding-bottom: 20px;
    border-top: 1px solid #fff;
}

.powered {
    font-weight: bold;
    color: #DDD;
    font-style: italic;
    padding: 10px 0 10px 0;
    font-size: 1.1em;
}

.container-fluid, .container {
    padding-right: 15px;
    padding-left: 15px;
    margin-right: auto;
    margin-left: auto;
}

.text-right {
    text-align: right;
}

.pull-right {
    float: right!important;
}

.row {
    margin-right: -15px;
    margin-left: -15px;
}

.offset-xs-1 {
    margin-left: 8.33333333%;
}

.col-2 {
    width: 16.66666667%;
}

.col-3 {
    width: 25%;
}

.col-4 {
    width: 33.33333333%;
}

.col-5 {
    width: 41.66666667%;
}

.col-6 {
    width: 50%;
}

.col-7 {
    width: 58.33333333%;
}

.col-9 {
    width: 75%;
}

.col-12 {
    width: 100%;
}

.weather-col {
    float: left;
    position: relative;
    min-height: 1px;
    padding-right: 15px;
    padding-left: 15px;
}
