css:
body {
    font-family: "Helvetica", "Arial", sans-serif;
    line-height: 1;
}

.container {
    background-color: #000;
    width: 189px !important;
    height: 900px !important;
    color: #fff;
    padding-top: 30px;
}

.current-condition {
    font-weight: bold;
    font-size: 23px;
}

.info1 {
    font-size: 14px;
    padding: 15px 0px;
}

.info2 {
    padding-bottom: 10px;
}

.day {
    font-size: 18px;
    font-weight: lighter;
    font-style: italic;
}

.icon {
    font-size: 50px;
    padding: 20px 0px;
}

.temp {
    font-size: 15px;
}

.border {
    padding-top: 40px;
    padding-bottom: 20px;
    border-top: 1px solid #fff;
}

.powered {
    font-weight: bold;
    color: #DDD;
    font-style: italic;
    padding: 10px 0 10px 0;
    font-size: 1.1em;
}
