css:
body {
 width: 250px !important;
 height: 250px !important;
 }

#content {
    color: #f9f9f9;
}

.shadowed {
text-shadow: 0px 0px 6px rgba(0, 0, 0, 0.6);
    filter: dropshadow(color=rgba(0, 0, 0, 0.6), offx=6, offy=6);
}

.main-container {
 padding-bottom: 8px;
 }

.top-content {
 height: 110px;
}

.bottom-content {
 height: 90px;
}

.row-element-half {
    display: inline-block;
    width: 50%;
    vertical-align: top;
}

.date {
    font-weight: 900;
    font-size: 20px;
    text-transform: uppercase;
    padding: 12px 0px 0px 12px;
}

.temp {
    font-size: 35px;
    font-weight: 900;
    padding: 12px 0px 0px 12px;
}

.temp sup {
 font-size: 15px;
 }

.main-icon {
    line-height: 84px;
    font-size: 60px;
    text-align: right;
    padding: 12px;
}

.bg-footer {
    background-color: #111;
    background-color: #111a;
    padding: 10px 8px;
    font-size: 13px;
}

.forecast-current {
    margin-bottom: 4px;
    font-size: 14px;
    overflow: hidden;
}

.powered {
  padding: 4px 0;
  color: white;
  font-size: 13px;
 text-align: center;
  background-color: #222;
}
