css:
body {
  width: 1080px !important;
 height: 1920px !important;
 background-color: [background-color];
}

#content, .menu-board-parent-container {
  position: relative;
 width: 1080px;
height: 1920px;
}

.menu-board-parent-container {
  background: [secondary-color];
}

.menu-board-name {
 position: absolute;
 color: [header-font-color];
 background: [header-color];
 font-weight: bold;
 font-size: 3rem;
 text-align: center;
 height: 180px;
 left: 0;
 top: 0;
 width: calc(50% - 0.5rem);
 display: flex;
 align-content: center;
 flex-direction: row;
 justify-content: center;
 align-items: center}

.menu-board-categories-container {
  height: 100%;
  column-gap: 1rem; color: [font-color];
}

.menu-board-zone {
  font-size: 1.4rem;
  padding: 0.8rem;
 display: flex;
 flex-direction: column; background-color: [background-color]
 }

.menu-board-category-header {
 font-size: 0;
 color: #eeeeee;
 margin: -0.8rem -0.8rem 0rem -0.8rem;
 display: flex;
 flex-direction: column;
 border-bottom: 1rem solid [secondary-color];
}

.menu-board-category-header-name {
 font-size: 2rem;
 text-align: center;
 font-weight: bold;
 padding: 0.4rem 0;
 background-color: [primary-color];
}

.menu-board-category-image {
  width: 100%;
  height: 6rem;
 object-fit: cover;
  object-position: center;
}

.menu-board-product {
  padding-top: 2rem;
  display: grid;
 row-gap: 2px;
  grid-template: "f a b""f c b""f d d""f e e" / 0fr 1fr 1fr;
 align-content: start;
}

.menu-board-products-container {
 height: 100%;
 overflow: hidden;
 }

.menu-board-product-name {
  grid-area: a;
  font-size: 1.5rem;
  font-weight: bold;
}

.menu-board-product-image {
  grid-area: f; width: 120px;
  height: 100px;
 margin-right: 0.5rem;
 object-fit: cover;
  object-position: center;
}

.menu-board-product-price {
  grid-area: b;
  font-size: 1.6rem;
  text-align: right;
}

.menu-board-product-description {
  grid-area: d;
  font-size: 1.2rem;
 padding-bottom: 0.25rem;
 min-height: 1.2rem;
}

.menu-board-product-allergy {
  grid-area: c;
  color: [secondary-color];
}

.menu-board-product-options-container {
  grid-area: e;}

.menu-board-product-options { font-size: 1rem;
  color: [primary-color]; display: block;
 margin-right: 1rem;
}

.menu-board-product.product-unavailable .menu-board-product-name, .menu-board-product.product-unavailable .menu-board-product-price, .menu-board-product.product-unavailable .menu-board-product-description, .menu-board-product.product-unavailable .menu-board-product-allergy, .menu-board-product.product-unavailable .menu-board-product-options {
  color: [disabled-color];
}

.menu-board-product.product-highlight .menu-board-product-name, .menu-board-product.product-highlight .menu-board-product-price {
  font-size: 1.8rem;
 color: [highlight-color];
  font-weight: bold;
}
