css:
body {
    font-family: "Helvetica", "Arial", sans-serif;
    line-height: 1;
}

.container-main {
    height: 420px !important;
    width: 820px !important;
}

.container {
    width: 820px !important;
    height: 420px !important;
    float: left;
    margin-top: 20px;
    padding-right: 15px;
    padding-left: 15px;
    margin-right: auto;
    margin-left: auto;
}

.row-finance {
    height: 40px;
    background: #e0e0e0;
    margin-bottom: 17px;
    border-left: #264a88 10px solid;
}

.row {
    margin-right: 0;
    margin-left: 0;
}

#cycle-container {
    margin-left: -15px;
    margin-right: -15px;
}

.value {
    font-size: 19px;
    padding-top: 10px;
    color: #000;
    white-space: nowrap;
}

.down-arrow {
    margin: 15px 0px 0px 20px;
    width: 0;
    height: 0;
    border-left: 10px solid transparent;
    border-right: 10px solid transparent;
    border-top: 10px solid red;
}

.up-arrow {
    margin: 15px 0px 0px 20px;
    width: 0;
    height: 0;
    border-left: 10px solid transparent;
    border-right: 10px solid transparent;
    border-bottom: 10px solid green;
}

.right-arrow {
    margin: 10px 0px 0px 25px;
    width: 0;
    height: 0;
    border-left: 10px solid gray;
    border-top: 10px solid transparent;
    border-bottom: 10px solid transparent;
}

.variant {
    font-size: 20px;
    padding-top: 17px;
}

.flags {
    padding-top: 4px;
}

.value-up {
    color: green;
    font-weight: bold
}

.value-down {
    color: red;
    font-weight: bold
}

.value-equal {
    color: gray;
    font-weight: bold
}

.name-stock {
    color: gray;
}

.col-1 {
    width: 8.33333333%;
}

.col-2 {
    width: 16.66666667%;
}

.col-5 {
    width: 41.66666667%;
}

.weather-col {
    float: left;
    position: relative;
    min-height: 1px;
    padding-right: 15px;
    padding-left: 15px;
}
