css:
body {
    font-family: "Helvetica", "Arial", sans-serif;
    line-height: 1;
}

.container-main {
    height: 380px !important;
    width: 500px !important;
}

.container {
    width: 500px !important;
    height: 380px !important;
    float: left;
    margin: 0;
    padding: 0;
}

.row {
    margin: 0;
}

.value {
    font-size: 20px;
    padding-top: 10px;
    color: #000;
}

.value-currency {
    font-size: 45px;
    text-align: right;
    margin-bottom: 40px;
}

.value-up {
    color: green;
    font-weight: bold
}

.value-down {
    color: red;
    font-weight: bold
}

.value-equal {
    color: gray;
    font-weight: bold
}

.variant {
    font-size: 20px;
    padding-top: 17px;
}

.flags {
    padding-top: 4px;
}

.live-title {
    font-size: 30px;
    font-weight: bold;
}

.time-title {
    font-size: 14px;
    padding-top: 10;
}

.name-stock {
    color: gray;
}

.name {
    font-size: 40px;
    margin-top: 40px;
}

.subname {
    color: gray;
    font-size: 35px;
    margin-bottom: 30px;
}

.total {
    margin-top: 30px;
    font-size: 80px;
    font-weight: bold;
}

.subtotal {
    display: inline-block;
}

.down-arrow {
    margin: 0px 0px 10px 0px;
    width: 0;
    height: 0;
    border-left: 30px solid transparent;
    border-right: 30px solid transparent;
    border-top: 30px solid red;
}

.up-arrow {
    margin: 0px 0px 10px 0px;
    width: 0;
    height: 0;
    border-left: 30px solid transparent;
    border-right: 30px solid transparent;
    border-bottom: 30px solid green;
}

.right-arrow {
    margin: 0px 0px 0px 10px;
    width: 0;
    height: 0;
    border-left: 30px solid gray;
    border-top: 30px solid transparent;
    border-bottom: 30px solid transparent;
}

.col-12 {
    width: 100%;
}

.weather-col {
    float: left;
    position: relative;
    min-height: 1px;
    padding-right: 15px;
    padding-left: 15px;
}

.logo {
    height: 40px;
}
