css:
html {
 /* Global font size, change for font scale */
 font-size: 16px;
 }

 #content, .item, .image, .image img {
  width: 100%;
  height: 100%;
}

.image img {
  object-fit: cover;
}

.cycle-overlay {
 display: flex;
 flex-direction: column;
 justify-content: center;
 color: white;
  background: black;
  opacity: .6;
  filter: alpha(opacity=60);
  position: absolute;
  bottom: 0;
  top: 0px;
  width: 20%;
  height: 100%;
  padding: 0.8rem;
}

.cycle-overlay > div {
  font-family: Arial, Verdana, sans-serif;
}

.cycle-overlay .title {
  font-size: 1.2rem;
  color: white;
  font-weight: bold;
}

.cycle-overlay .description {
  font-size: 1rem;
  margin-top: 1rem;
}
