css:
html {
 /* Global font size, change for font scale */
 font-size: 16px;
 }

 #content, .item, .image, .image img {
  width: 100%;
  height: 100%;
}

.image img {
  object-fit: cover;
}

.cycle-overlay {
 color: white;
  background: black;
  opacity: .6;
  filter: alpha(opacity=60);
  position: absolute;
  bottom: 0;
  width: 100%;
  padding: 2rem 0;
 text-align: center;
 }

.cycle-overlay > div {
  font-family: Arial, Verdana, sans-serif;
}

.cycle-overlay .title {
  font-size: 1.2rem;
  color: white;
  font-weight: bold;
}
