css:
.item {
    float: left;
}

body {
    font-family: "Helvetica", "Arial", sans-serif;
    line-height: 1;
}

body {
    background-repeat: no-repeat;
    background-size: cover;
    background-position: center;
}

.emojione {
    width: 30px;
    height: 30px
}

.pic-container img {
    width: 300px;
    height: 300px;
}

.pic-container {
    position: relative;
    width: 270px;
    height: 270px;
    overflow: hidden;
}

.pic-container img {
    position: absolute;
    left: 50%;
    top: 50%;
    height: 100%;
    width: auto;
    -webkit-transform: translate(-50%, -50%);
    -ms-transform: translate(-50%, -50%);
    transform: translate(-50%, -50%);
}

.page {
    width: auto;
    height: auto;
}

.twitter-container {
    width: 960px !important;
    height: 350px !important;
    padding-top: 35px;
    padding-bottom: 40px;
    border: 1px solid #eee;
    background-color: rgba(255, 255, 255, 0.6);
    background: rgba(255, 255, 255, 0.6);
    color: rgba(255, 255, 255, 0.6);
}

.main-tweet {
    font-size: 30px;
    padding-top: 30px;
    line-height: 120%;
    color: #434343;
}

.twitter-date, .twitter-location {
    margin-top: 20px;
    font-size: 17px;
    color: #434343;
    font-weight: bold;
}
