css:
.item {
    float: left;
}

.emojione {
    width: 30px;
    height: 30px
}

body {
    font-family: "Helvetica", "Arial", sans-serif;
    line-height: 1;
    background-image: url('../clouds.jpg');
    background-repeat: no-repeat;
    background-size: cover;
    background-position: center;
}

.img-container img {
    width: 50px;
    height: 50px;
    border: 3px solid #fff;
}

.twitter-container {
    padding: 10px;
    width: 340px !important;
    height: 200px !important;
    background-color: rgba(255, 255, 255, 0.6);
    background: rgba(255, 255, 255, 0.6);
    color: rgba(255, 255, 255, 0.6);
}

.main-tweet {
    font-size: 14px;
    padding: 10px 0px;
    line-height: 120%;
    color: #434343;
}

.mini-widget {
    height: 180px;
    padding-top: 20px;
    border: 1px solid #eee;
    background-color: #fff;
}

.twitter-date, .twitter-location {
    margin-top: 30px;
    font-size: 17px;
    color: #434343;
    font-weight: bold;
}

.name {
    font-size: 20px;
    font-weight: bold;
    color: #434343;
    margin-top: 5px;
}

.username {
    margin-top: -4px;
    color: #434343;
}

.avatar {
    border: 3px solid #fff;
}

.row {
    margin-right: -15px;
    margin-left: -15px;
}

.col-2 {
    width: 16.66666667%;
}

.col-10 {
    width: 83.33333333%;
}


.col-12 {
    width: 100%;
}

.twitter-col {
    float: left;
    position: relative;
    min-height: 1px;
    padding-right: 15px;
    padding-left: 15px;
}
