css:
.item {
    float: left;
}

.emojione {
    width: 10px;
    height: 10px
}

body {
    font-family: "Helvetica", "Arial", sans-serif;
    line-height: 1;
}

.nopic {
    height: 160px !important;
    width: 400px !important;
    float: left;
    border: 1px solid #ddd;
}

.twitpic {
    height: 450px !important;
    width: 400px !important;
    float: left;
    border: 1px solid #ddd;
    background: #FFF;
}

.main-tweet {
    font-size: 14px;
    padding: 30px;
    line-height: 140%;
    color: #434343;
    height: 112px;
    text-align: justify;
}

.mainpic {
    height: 300px;
    width: 400px;
}

.mainpic img {
    display: block;
    width: 400px;
    clip: rect(0px, 398px, 299px, 0px);
    position: absolute;
}

.footer {
    background: #1da1f2;
    width: 100%;
    height: 37px;
}

.user {
    font-size: 14px;
    font-weight: bold;
    color: #fff;
    margin-top: 9px;
    letter-spacing: 1px;
    padding-left: 0px;
}
