css:
.item {
    float: left;
}

.emojione {
    width: 10px;
    height: 10px
}

body {
    font-family: "Helvetica", "Arial", sans-serif;
    line-height: 1;
}

.nopic {
    height: 160px !important;
    width: 400px !important;
    float: left;
    border: 1px solid #ddd;
}

.twitpic {
    height: 450px !important;
    width: 400px !important;
    float: left;
    border: 1px solid #ddd;
    background: #FFF;
}

.main-tweet {
    font-size: 14px;
    padding: 15px 20px;
    line-height: 140%;
    color: #434343;
    height: 112px;
    text-align: justify;
    overflow: hidden;
}

.mainpic {
    height: 300px;
    width: 400px;
}

.mainpic img {
    display: block;
    width: 400px;
    clip: rect(0px, 398px, 299px, 0px);
    position: absolute;
}

.footer {
    background: #1da1f2;
    width: 100%;
    height: 37px;
}

.user {
    font-size: 14px;
    font-weight: bold;
    color: #fff;
    margin-top: 9px;
    letter-spacing: 1px;
    padding-left: 0px;
}


.row {
    margin-right: -15px;
    margin-left: -15px;
}

.col-2 {
    width: 16.66666667%;
}

.col-10 {
    width: 83.33333333%;
}


.col-12 {
    width: 100%;
}

.twitter-col {
    float: left;
    position: relative;
    min-height: 1px;
    padding-right: 15px;
    padding-left: 15px;
}
