css:
.item {
    float: left;
}

.emojione {
    width: 10px;
    height: 10px
}

body {
    font-family: "Helvetica", "Arial", sans-serif;
    line-height: 1;
}

.image-div {
    overflow: hidden;
    height: 110px;
    padding: 0;
}

.image-div IMG {
    border-radius: 50%;
    position: absolute;
    width: 70px;
    top: 50%;
    left: 50%;
    transform: translate(-50%,-50%);
-webkit-transform: translate(-50%, -50%);
    border: 3px solid #FFF;
}

.twit {
    height: 650px !important;
    width: 350px !important;
    float: left;
    border: 1px solid #ddd;
    background: #FFF;
}

.main-tweet {
    font-size: 25px;
    padding: 40px;
    line-height: 160%;
    color: #4d4d4d;
    height: 480px;
}

.header {
    background: #1da1f2;
    width: 100%;
    height: 110px;
    border: 1px solid #ddd;
}

.user {
    font-size: 20px;
    font-weight: bold;
    color: #fff;
    margin-top: 40px;
    letter-spacing: 1px;
}

.tiempo {
    font-weight: lighter;
    font-size: 20px;
    margin-top: 15px;
}
