css:
body {
    font-family: "Helvetica", "Arial", sans-serif;
    line-height: 1;
    margin: 0;
}

#content {
    width: 1080px !important;
    height: 1920px !important;
    background: rgba(255, 255, 255, 0.6);
    color: rgba(255, 255, 255, 1);
}

.row-1 {
    height: 320px;
}

.page {
    float: left;
    margin: 0;
    padding: 0;
}

.cell-1 {
    width: 350px;
}

.cell-2 {
    width: 710px;
}

.cell-3 {
    width: 1070px;
}

.cell-1, .cell-2, .cell-3 {
    float: left;
    height: inherit;
    margin: 5px;
    background-repeat: no-repeat;
    background-size: cover;
    background-position-x: 50%;
    background-position-y: 50%;
}

.item-container {
    padding: 10px;
    color: #fff;
    height: 310px;
}

.userData {
    height: 50px;
}

.darken-container {
    background-color: rgba(0, 0, 0, 0.4);
}

.tweet-profilePic {
    width: 20%;
    float: left;
}

.tweet-profilePic img {
    width: 48px;
}

.tweet-userData {
    width: 80%;
    float: left;
    text-align: right;
}

.item-text {
    padding: 10px;
    color: #fff;
}

.emojione {
    width: 26px;
    height: 26px;
}

.cell-1 .item-text {
    line-height: 30px;
    font-size: 25px;
    height: 240px;
}

.cell-2 .item-text {
    line-height: 40px;
    font-size: 40px;
    height: 240px;
}

.cell-3 .item-text {
    line-height: 53px;
    font-size: 50px;
    height: 240px;
}

.user {
    font-size: 14px;
    font-weight: bold;
    padding-top: 20px;
}

.shadow {
    text-shadow: 1px 1px 2px rgba(0, 0, 3, 1);
}

.no-shadow {
    text-shadow: none !important;
}

small {
    font-size: 70%;
}
