css:
#content {
    color: #fff;
}

.info .block-container {
    display: inline-block;
    height: 110px;
    width: 50%;
}

.shadowed {
    text-shadow: 0px 0px 5px rgba(0, 0, 0, 0.4);
    filter: dropshadow(color=rgba(0, 0, 0, 0.4), offx=5, offy=5);
}

.date {
    font-family: 'Roboto', sans-serif;
    font-weight: 900;
    font-size: 30px;
    text-transform: uppercase;
    float: left;
    padding: 4px;
}

.temp {
    font-family: 'Roboto', sans-serif;
    font-size: 70px;
    font-weight: 900;
    line-height: 70px;
    float: right;
    text-align: right;
    padding: 4px;
}

.main-icon-container {
    text-align: center;
}

.main-icon {
    font-size: 0;
}

.main-icon i {
    font-size: 110px;
}

.footer-powered-by {
    color: #f9f9f9;
    background-color: #000;
    opacity: 0.6;
}
